import React, { useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import About from "./components/About";
import Projects from "./components/Projects";
import Skills from "./components/Skills";
import Contact from "./components/Contact";
import Education from "./components/Education";
import "./App.css";

function App() {
  const [activeSection, setActiveSection] = useState("about");

  return (
    <div className="App">
      <header>
        <h1>Adham Haitham's Portfolio</h1>
        <nav>
          <a href="#about" onClick={() => setActiveSection("about")}>
            About
          </a>
          <a href="#projects" onClick={() => setActiveSection("projects")}>
            Projects
          </a>
          <a href="#skills" onClick={() => setActiveSection("skills")}>
            Skills
          </a>
          <a href="#education" onClick={() => setActiveSection("education")}>
            Education
          </a>
          <a href="#contact" onClick={() => setActiveSection("contact")}>
            Contact
          </a>
        </nav>
      </header>
      <main>
        <TransitionGroup>
          <CSSTransition key={activeSection} timeout={300} classNames="fade">
            <div>
              {activeSection === "about" && <About />}
              {activeSection === "projects" && <Projects />}
              {activeSection === "skills" && <Skills />}
              {activeSection === "education" && <Education />}
              {activeSection === "contact" && <Contact />}
            </div>
          </CSSTransition>
        </TransitionGroup>
      </main>
    </div>
  );
}

export default App;
